import React, { useContext, useState } from "react";
import { CopyToClipboard } from "react-copy-to-clipboard";
import {
  Button,
  TextField,
  Grid,
  Typography,
  Container,
  Paper,
} from "@mui/material";
import { Assignment, Phone, PhoneDisabled } from "@mui/icons-material";
import { SocketContext } from "../SocketContext";

const Options = ({ children }) => {
  const { me, name, callAccepted, setName, callEnded, leaveCall, callUser } =
    useContext(SocketContext);
  const [idToCall, setIdToCall] = useState("");

  return (
    <Container
      sx={{
        width: 600,
        margin: "35px 0",
        padding: 0,
        "@media (max-width: 600px)": { width: "80%" },
      }}
    >
      <Paper
        elevation={10}
        sx={{ padding: "10px 20px", border: "2px solid black" }}
      >
        <form
          noValidate
          autoComplete="off"
          sx={{ display: "flex", flexDirection: "column" }}
        >
          <Grid container sx={{ width: "100%", flexDirection: "column" }}>
            <Grid item xs={12} md={6} sx={{ padding: 20 }}>
              <Typography gutterBottom variant="h6">
                Account Info
              </Typography>

              <TextField
                label="Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                fullWidth
              />

              <CopyToClipboard text={me} sx={{ marginTop: 20 }}>
                <Button
                  variant="contained"
                  color="primary"
                  fullWidth
                  startIcon={<Assignment />}
                  fontSize="large"
                />
              </CopyToClipboard>
            </Grid>
          </Grid>
        </form>
      </Paper>
      Options
      {children}
    </Container>
  );
};

export default Options;
